import Link from 'next/link';
import fetch from 'isomorphic-unfetch';

const TopMenu = (props) => {
	console.warn('props', props);
	return (
		<div className="menu">
		   <div class="menu-item active" data-label="Bestsellers"><span>BESTSELLERS</span></div>
		   <div class="menu-item" data-label="Veg Pizza"><span>VEG PIZZA</span></div>
		   <div class="menu-item" data-label="Non-Veg Pizza"><span>NON-VEG PIZZA</span></div>
		   <div class="menu-item" data-label="Pizza Mania"><span>PIZZA MANIA</span></div>
		   <div class="menu-item" data-label="Sides"><span>SIDES</span></div>
		   <div class="menu-item"  data-label="Beverages"><span>BEVERAGES</span></div>
		   <div class="menu-item" data-label="Dessert"><span>DESSERT</span></div>
		</div>
	);
};

TopMenu.getInitialProps = async () => {
	const data = await fetch('http://localhost/pizzaapi/public/api/categories');
	return{
		categories : data
	} 
};
export default TopMenu;
